import "./App.css";
import Mains from "./components/Mains";
import Navbar from "./components/Navbar";
//import Char from "./components/Char";
//import About from "./About";
//step 1: There is too much code for resolving that issue just add components there
// <Mains name="my" />
// then import it here using export default, don't forget to return them in ();

//import React, { Component } from "react";

//new change=Used function based

import React, { useState } from "react";
//react router
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

export default function App() {
  //using state

  const [loggedIn, setLoggedIn] = useState(false);

  return (
    <>
      <div>
        {" "}
        <Navbar />
        <Router>
          <Routes>
            {loggedIn === false ? (
              <>
                {" "}
                <Route exact path="/">
                  "Pleasse Sign in"{" "}
                </Route>{" "}
              </>
            ) : (
              <>
                <Route exact path="/mains" element={<Mains />} />
              </>
            )}
          </Routes>{" "}
        </Router>
      </div>
      <button className="btn btn-primary" onClick={() => setLoggedIn(true)}>
        Sign In
      </button>
      <button className="btn btn-primary" onClick={() => setLoggedIn(false)}>
        Sign out{" "}
      </button>
    </>
  );
}
