import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register the required components for the chart
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);
// Register necessary components

export default function Char() {
  const [mapData, setMapData] = useState([]);

  useEffect(() => {});

  return (
    <>
      <Container className="my-4">
        <Row>
          <Col md={4} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>Pie Chart 1</Card.Title>
                {mapData && mapData.data && mapData.data.length > 0 && (
                  <Pie data={getData()} />
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>

      <Container className="my-4">
        <Row>
          <Col md={4} className="my-4">
            <Card>
              <Card.Body>
                <Card.Title>New Data </Card.Title>
                <Pie
                  data={{
                    label: ["Red", "Yellow", "green"],
                    datasets: [
                      {
                        labels: ["% of votes"],
                        data: [12, 19, 3],
                        backgroundColor: ["red", "yellow", "green"],
                      },
                    ],
                  }}
                />
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
}
