import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Button } from "react-bootstrap";

// Register the required components for the chart
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);
// Register necessary components
//let result;

export default function Char() {
  const [mapData, setMapData] = useState([]);
  //const [use, set] = useState(1);
  useEffect(() => {
    async function dataa() {
      const url = "https://target1.p.rapidapi.com/stores/v2/list?place=10009";
      const options = {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "6eb69c2aa1mshddf27456becc191p108768jsn5185fd3a034a",
          "x-rapidapi-host": "target1.p.rapidapi.com",
        },
      };
      const response = await fetch(url, options);
      const data = await response.json();
      setMapData(data);
      console.log(data);
    }
    dataa();
  }, []);
  let incr = 5,
    decr = 5;

  let labels = [],
    k = [],
    datasts = [];
  if (
    mapData.data &&
    mapData.data.nearby_stores &&
    mapData.data.nearby_stores.stores
  ) {
    k = mapData.data.nearby_stores.stores;
    labels = k.map((item) => item.location_name);
    console.log(labels);
    datasts = k.map((item) => item.distance);
    console.log(datasts);
  }

  const data = {
    labels: labels.splice(0, 20),
    datasets: [
      {
        label: "Api Data Fetched for Location distance wise ",
        data: datasts.splice(0, 20),
        borderColor: "rgba(75, 192, 192, 1)",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true, // Makes the chart responsive
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Nearby Store Distances",
      },
    },
    scales: {
      y: {
        beginAtZero: true, // y-axis starts at 0
      },
    },
  };

  return (
    <>
      <div
        style={{
          width: "80%",
          height: "400px",
          margin: "0 auto",
          padding: "20px",
          backgroundColor: "#f9f9f9",
          borderRadius: "10px",
          boxShadow: "4px 4px 15px rgba(15, 15, 15, 0.1)",
        }}
      >
        {" "}
        <Bar data={data} options={options} />
        <Button className="btn btn-primary-btn-dark" onClick={increment()}>
          Increment
        </Button>
        <Button className="btn btn-primary-btn-dark" onClick={decrement()}>
          Dcrement
        </Button>
      </div>
    </>
  );
}
