import React, { Component } from "react";
import About from "../About";

export class Mains extends Component {
  dataFile = [
    {
      article: "This is the main  article that is being used here",
      Author: "Virat Kohli",
      Image:
        "https://buffer.com/cdn-cgi/image/w=1000,fit=contain,q=90,f=auto/library/content/images/size/w1200/2023/10/free-images.jpg",
      url: "https://gratisography.com/",
    },
    {
      article: "Hey in this we will be discussing about Cricket",
      Author: "I am the author",
      Image:
        "https://static.toiimg.com/thumb/msid-113439778,imgsize-57990,width-400,resizemode-4/113439778.jpg",
      url: "https://timesofindia.indiatimes.com/sports/cricket/bangladesh-in-india/we-have-laid-out-some-plans-rohit-sharma-on-bowlers-workload-management-in-packed-test-schedule/articleshow/113439781.cms",
    },
    {
      article:
        "This whole genre is all about writing readable codes with logics",
      Author: "Ravi Shastri",
      Image:
        "https://statico.sportskeeda.com/editor/2024/09/85b12-17264903379946-1920.jpg",
      url: "https://www.sportskeeda.com/cricket/news-ravi-bhai-organized-team-dinner-arranged-karaoke-started-singing-ravichandran-ashwin-aftermath-india-s-adelaide-36-all-out",
    },
  ];

  constructor() {
    super();
    this.state = {
      dataFile: this.dataFile,
    };
  }

  render() {
    return (
      <div>
        <center>
          This is a news item
          {this.dataFile.map((element) => {
            return (
              <About
                title={element.Author.slice(0, 5)}
                desc={element.article.slice(0, 10)}
                imgurl={element.Image}
              />
            );
          })}
        </center>
      </div>
    );
  }
}

export default Mains;
