import React, { useState } from "react";

function changeBackground() {
  const color = document.getElementById("id1");
  if (color.className === "btn btn-dark mx-4") {
    setTimeout(() => {
      color.className = "btn btn-success mx-4";
    }, 200);
  } else {
    setTimeout(() => {
      color.className = "btn btn-dark mx-4";
    }, 200);
  }
}

function bg() {
  let id2 = document.getElementById("id2");
  if (id2.style.backgroundColor === "") {
    id2.style.backgroundColor = "lightgrey";
  } else {
    id2.style.backgroundColor = "";
  }
}

export default function Mains(props) {
  const [useClassName, setUseClassName] = useState("btn btn-dark mx-4");

  return (
    <>
      <div
        className="body"
        id="id2"
        align="center"
        onClick={bg}
        style={{
          width: "1000px",
          height: "200px",
          backgroundcolor: "lightgray",
        }}
      >
        <h1>This is {props.name} Page</h1>
      </div>

      <button
        id="id1"
        type="button"
        className="btn btn-dark mx-4"
        onClick={changeBackground}
      >
        Dark
      </button>
    </>
  );
}
