import React from "react";
import { Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

import { Card, Container, Row, Col } from "react-bootstrap";
// Register necessary components
ChartJS.register(ArcElement, Tooltip, Legend);

const data1 = {
  labels: ["Red", "Blue", "Yellow"],
  datasets: [
    {
      label: "Dataset 1",
      data: [300, 50, 100],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(255, 206, 86, 0.2)",
      ],
      borderColor: [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(255, 206, 86, 1)",
      ],
      borderWidth: 1,
    },
  ],
};

const data2 = {
  labels: ["Green", "Purple", "Orange"],
  datasets: [
    {
      label: "Dataset 2",
      data: [150, 250, 100],
      backgroundColor: [
        "rgba(75, 192, 192, 0.2)",
        "rgba(153, 102, 255, 0.2)",
        "rgba(255, 159, 64, 0.2)",
      ],
      borderColor: [
        "rgba(75, 192, 192, 1)",
        "rgba(153, 102, 255, 1)",
        "rgba(255, 159, 64, 1)",
      ],
      borderWidth: 1,
    },
  ],
};

const data3 = {
  labels: ["Red", "Blue", "Green"],
  datasets: [
    {
      label: "Dataset 3",
      data: [120, 180, 100],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(75, 192, 192, 0.2)",
      ],
      borderColor: [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(75, 192, 192, 1)",
      ],
      borderWidth: 1,
    },
  ],
};

export default function Char() {
  return (
    <Container className="my-4">
      <Row>
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <Card.Title>Pie Chart 1</Card.Title>
              <Pie data={data1} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <Card.Title>Pie Chart 2</Card.Title>
              <Pie data={data2} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <Card.Title>Pie Chart 3</Card.Title>
              <Pie data={data3} />
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
